import requests
import base64
import json

# Test image (a very small base64 encoded JPEG)
test_image = "data:image/jpeg;base64,"

# Test health endpoint
print("Testing health endpoint...")
health_response = requests.get("http://localhost:5000/health")
print(f"Health response: {health_response.status_code} - {health_response.text}")

# Test detect-face endpoint
print("\nTesting detect-face endpoint...")
detect_response = requests.post(
    "http://localhost:5000/detect-face",
    json={"image": test_image}
)
print(f"Detect face response: {detect_response.status_code} - {detect_response.text}")

# Test extract-embedding endpoint
print("\nTesting extract-embedding endpoint...")
embedding_response = requests.post(
    "http://localhost:5000/extract-embedding",
    json={"image": test_image}
)
print(f"Extract embedding response: {embedding_response.status_code} - {embedding_response.text}")

print("\nTests completed.")
